mapper.select:
SELECT books.bookId, books.isbn, books.bookName, books.author, books.publishId, books.typeId, books.blurb, books.imageUrl1, books.imageUrl2, books.imageUrl3, books.bookPrice, books.addTime, books.removeTime, books.state FROM bsms.books where state=0
and (bookName LIKE concat(concat('%', #{bookName}), '%') OR author LIKE concat(concat('%', #{bookName}), '%') OR isbn LIKE concat(concat('%', #{bookName}), '%') OR publishId like concat(concat('%', #{bookName}), '%') OR typeId like concat(concat('%', #{bookName}), '%'))
LIMIT #{page.pageStart},#{page.pageSize}

mapper.update:
update bsms.books
set state=1,removeTime=NOW()
where bookId= #{bookId}

	UPDATE books set
	isbn=#{isbn}
	,bookName=#{bookName}
	,author=#{author}
	,publishId=#{publishId}
    ,typeId=#{typeId}
    ,blurb=#{blurb}
    ,imageUrl1=#{imageUrl1}
    ,imageUrl2=#{imageUrl2}
    ,imageUrl3=#{imageUrl3}
    ,bookPrice=#{bookPrice}
    WHERE
    books.bookId=#{bookId}
